mapper.delete:
DELETE 
FROM WORKPLACE
WHERE  store_code = #{STORE_CODE} AND USER_CODE_PTJ = #{USER_CODE_PTJ}

DELETE
FROM PART_TIMER
WHERE store_code = #{STORE_CODE} AND USER_CODE_PTJ = #{USER_CODE_PTJ}

mapper.update:
UPDATE REQUEST_SCHEDULE SET
	ACCEPT_PTJ = 'D'
WHERE reqschedule_number = #{REQ_CODE}
